def sign(x):
    y = 0
    if x == '6':
        y = -1
    elif x == '9':
        y = 0
    elif x == '1':
        y = 1
    return y


def xs(x, y, w):
    if len(x) == len(y) == len(w):
        num1 = 0
        num2 = 0
        for k in range(len(x)):
            num1 += x[k] * y[k] * w[k]
            num2 += abs(x[k] * y[k] * w[k])
        if num2 == 0:
            return 0
        else:
            return num1 / num2
    else:
        print('error')


def fh(x):
    if float(x) > 0:
        return 1
    else:
        return -1


