print(w1)
for k in range(n2):
    ph = 0
    cs = -1
    for v in range(n2):
        f1 = 0
        f2 = 0
        f3 = 0
        f4 = 0
        f5 = 0
        for i in range(n1 - 1):
            if dic0[i][1][k] == 0 or dic0[i][1][v] == 0:
                continue
            else:
                for j in range(i + 1, n1):
                    if dic0[j][1][v] == 0 or dic0[j][1][k] == 0:
                        continue
